import * as Naruse from 'Naruse'
import { getParameterError } from '../../utils'
import { MethodHandler, temporarilyNotSupport } from '../../../../naruse-share/index'
import Toast from './toast'

// 交互

let status = 'default'

// inject necessary style
function init (doc) {
  if (status === 'ready') return

  const taroStyle = doc.createElement('style')
  taroStyle.textContent = '@font-face{font-weight:normal;font-style:normal;font-family:"taro";src:url("data:application/x-font-ttf;charset=utf-8;base64, ") format("truetype");}@-webkit-keyframes taroLoading{0%{-webkit-transform:rotate3d(0, 0, 1, 0deg);}100%{-webkit-transform:rotate3d(0, 0, 1, 360deg);transform:rotate3d(0, 0, 1, 360deg);}}@keyframes taroLoading{0%{-webkit-transform:rotate3d(0, 0, 1, 0deg);}100%{-webkit-transform:rotate3d(0, 0, 1, 360deg);transform:rotate3d(0, 0, 1, 360deg);}}.taro-modal__foot:after {content: "";position: absolute;left: 0;top: 0;right: 0;height: 1px;border-top: 1px solid #D5D5D6;color: #D5D5D6;-webkit-transform-origin: 0 0;transform-origin: 0 0;-webkit-transform: scaleY(0.5);transform: scaleY(0.5);} .taro-model__btn:active {background-color: #EEEEEE}.taro-model__btn:not(:first-child):after {content: "";position: absolute;left: 0;top: 0;width: 1px;bottom: 0;border-left: 1px solid #D5D5D6;color: #D5D5D6;-webkit-transform-origin: 0 0;transform-origin: 0 0;-webkit-transform: scaleX(0.5);transform: scaleX(0.5);}.taro-actionsheet__cell:not(:first-child):after {content: "";position: absolute;left: 0;top: 0;right: 0;height: 1px;border-top: 1px solid #e5e5e5;color: #e5e5e5;-webkit-transform-origin: 0 0;transform-origin: 0 0;-webkit-transform: scaleY(0.5);transform: scaleY(0.5);}'
  doc.querySelector('head').appendChild(taroStyle)

  status = 'ready'
}

const toast = new Toast()

const showToast: typeof Naruse.showToast = (options = { title: '' }) => {
  init(document)
  options = Object.assign({
    title: '',
    icon: 'success',
    image: '',
    duration: 1500,
    mask: false
  }, options)
  const { success, fail, complete } = options
  const handle = new MethodHandler({ name: 'showToast', success, fail, complete })

  if (typeof options.title !== 'string') {
    return handle.fail({
      errMsg: getParameterError({
        para: 'title',
        correct: 'String',
        wrong: options.title
      })
    })
  }

  if (typeof options.duration !== 'number') {
    return handle.fail({
      errMsg: getParameterError({
        para: 'duration',
        correct: 'Number',
        wrong: options.duration
      })
    })
  }

  if (options.image && typeof options.image !== 'string') options.image = ''

  options.mask = !!options.mask

  let errMsg = ''
  if (!toast.el) {
    errMsg = toast.create(options, 'toast')
  } else {
    errMsg = toast.show(options, 'toast')
  }
  return handle.success({ errMsg })
}

const hideToast: typeof Taro.hideToast = ({ success, fail, complete } = {}) => {
  const handle = new MethodHandler({ name: 'hideToast', success, fail, complete })
  if (!toast.el) return handle.success()
  toast.hide(0, 'toast')
  return handle.success()
}



export {  hideToast, showToast }
